  const bin = item.binary.voiceover || item.binary.data;

  if (bin) {
    bin.fileName = bin.fileName || 'voiceover.mp3';
    bin.mimeType = bin.mimeType || 'audio/mpeg';

    // force la pr√©sence sous 'voiceover' pour Telegram
    item.binary.voiceover = bin;
  }
}

return items;

// Merge tous les binaires dans un seul item + construit un array image_urls + √©vite tout usage de .item
let caption = '';
try {
  const desc = $items('Generate Description for Tiktok and Instagram', 0, 0);
  caption = (desc?.[0]?.json?.message?.content ?? desc?.[0]?.json?.caption ?? '').toString();
} catch (e) {
  caption = (items?.[0]?.json?.caption ?? '').toString();
}

const imageUrls = [];
const mergedBinary = {};

for (let i = 0; i < items.length; i++) {
  const it = items[i];

  // 1) URL (si tu en as une par item)
  const url = (it?.json?.image_url ?? it?.json?.url ?? '').toString().trim();
  if (url) imageUrls.push(url);

  // 2) Binaire: on renomme syst√©matiquement en photo1..photoN
  const bin = it?.binary ?? {};
  const firstKey = Object.keys(bin)[0];
  if (firstKey && bin[firstKey]) {
    mergedBinary[`photo${i + 1}`] = bin[firstKey];
  }

  // 3) Si l'item a d√©j√† des cl√©s photoX, on les garde aussi
  for (const [k, v] of Object.entries(bin)) {
    if (k.startsWith('photo') && v) mergedBinary[k] = v;
  }
}

return [
  {
    json: {
      ...(items[0]?.json || {}),
      caption,
      image_urls: imageUrls
    },
    binary: mergedBinary,
    pairedItem: items.map((_, idx) => ({ item: idx }))
  }
];

